mapper.insert:

select
    case
        when (max(substr(eq_code, 2, 8))) = to_char(sysdate, 'yyyyMMdd') then ('E' || to_char(max(substr(eq_code, 2, 13)) + 1))
        else ('E' || to_char(sysdate, 'yyyyMMdd') || to_char(lpad(1, 4, 0)) )
    end    eq_code
from    eq

  	INSERT INTO eq
  				(
	    	    eq_code
  			  , eq_mdnm
	     	  , eq_mdno
	     	  , use
	     	  , eq_type
	     	  , eq_name
	     	  , eq_min
	     	  , eq_max
	     	  , manager
	     	  , eq_location
	     	  , eq_img
	     	  , line_code
  				)
  	VALUES (
  			 #{eqCode}
  			,#{eqMdnm}
  			,#{eqMdno}
  			,#{use}
  			,#{eqType}
  			,#{eqName}
  			,#{eqMin}
  			,#{eqMax}
  			,#{manager}
  			,#{eqLocation}
  			,#{eqImg}
  			,#{lineCode}
  			)

mapper.update:
UPDATE eq
SET
	   eq_type = #{eqType}
	 , eq_name = #{eqName}
	 , eq_min = #{eqMin}
	 , eq_max = #{eqMax}
	 , use = #{use}
WHERE eq_code = #{eqCode}

UPDATE eq
SET	 
	   eq_mdnm = #{eqMdnm}
	 , eq_mdno = #{eqMdno}
	 , eq_location = #{eqLocation}
	 , line_code = #{lineCode}
	 , manager = #{manager}
	 , use = #{use}
WHERE eq_code = #{eqCode}

UPDATE eq
SET use = #{use}
WHERE eq_code = #{eqCode}
