mapper.update:
update 	prd
set		bom_code = #{bomCode}
where	prd_code = #{prdCode}

update 	bom
set		prd_code = #{prdCode}
where	bom_code = #{bomCode}

update 	bom
set		prd_code = null
where	prd_code = #{prdCode}

mapper.insert:

			select 	f_code_pk('bom','b') bom_code
			from	dual


	    INSERT INTO bom (
	    	bom_code
	    	,description
	    	,reg_date
	    	,manager
	    	,prd_code
    	)
	    VALUES (
	    	#{bomCode}
	    	,#{description}
	    	,#{regDate}
	    	,#{manager}
	    	,#{prdCode}

	    )

	    INSERT INTO b_detail (
	    	B_DETAIL_CODE
	    	,BOM_CODE 
	    	,MT_CODE 
	    	,QUANTITY_REQUIRED
	    )VALUES(
	    	f_code_pk('b_detail','bd')
			,(
				SELECT  bom_code
				FROM    (
				    SELECT 	*
				    FROM 	bom
				    ORDER BY bom_code desc
				)
				WHERE   ROWNUM = 1
       		)
       		,#{mtCode}
	    	,#{quantityRequired}
	    )
