mapper.insert:

	select 'MT' || lpad(to_char(NVL(max(substr(mt_code, 3, 3)), 0) + 1), 3, 0)
	from 	mt

    INSERT INTO mt (
    	mt_code
    	,mt_name
    	,material_type
    	,specification
    	,unit
    	,unit_price
    	,safety_stock
    	,comm
    	,stc_code
    	,update_date
    	,current_stc

    ) VALUES (
    	#{mtCode}
    	,#{mtName}
    	,#{materialType}
    	,#{specification}
    	,#{unit}
    	,#{unitPrice}
    	,#{safetyStock}
    	,#{comm}
    	,#{stcCode}
    	,SYSDATE
    	,#{currentStc}
    )

mapper.update:
    	update	mt
    	set
	    	mt_name = #{mtName}
	    	,material_type = #{materialType}
	    	,specification = #{specification}
	    	,unit = #{unit}
	    	,unit_price = #{unitPrice}
	    	,safety_stock = #{safetyStock}
	    	,comm = #{comm}
	    	,stc_code = #{stcCode}
	    	,update_date = SYSDATE
	    	,current_stc = #{currentStc}
		where mt_code = #{mtCode}
