mapper.insert:
UPDATE ORD
SET    ord_status = #{ordStatus}
WHERE  ord_code = #{ordCode}

mapper.select:
SELECT COALESCE(SUM(stc_quantity), 0)
FROM   prd_management
WHERE  prd_code = #{prdCode}

mapper.update:
UPDATE ord
SET ord_status = '출고완료'
WHERE ord_code = #{ordCode}

UPDATE prd_management
SET    out_quantity = out_quantity + #{outQuantity}
WHERE  lot = #{lot}
